import axios from "axios";

export const LoginUser = (data: any) => {
  axios
    .post("https://reqres.in/api/login", data)
    .then((response) => {
      if (response.data) {
        localStorage.setItem("token", response.data.token);
        alert("Sesion iniciada");
      }
    })
    .catch((error) => {
      if (error) {
        alert("Error al ingresar, valide los datos");
      }
    });
};
