import { Redirect, Route } from "react-router-dom";

type RouterProps = {
  component: any;

};

export const PrivateRouter = ({ component: Component, ...options }: RouterProps) => {
  let loginAccess = localStorage.getItem("token");
  let link = localStorage.getItem("_link_")
    ? localStorage.getItem("_link_")
    : null;

  const deleteToken = () => {
    localStorage.removeItem("_link_");
  };

  return (
    <Route {...options}>
      {loginAccess ? <Component /> : link ? deleteToken : <Redirect to="/" />}
    </Route>
  );
};

export const PublicRouter = ({ component: Component, ...options }: RouterProps) => {
  return (
    <Route {...options}>
      <Component />
    </Route>
  );
};
